mapper.select:
SELECT  FROM app_menu su where 1 = 1
  and su.id = #{id}  
  and su.name = #{name}  
  and su.href = #{href}  
  and su.create_time = #{create_time}  
  and su.update_time = #{update_time}  
  and su.roil_id = #{roil_id}

