mapper.insert:
insert into province_num_manage (
name,
value
 ) values (
#{name},
#{value}
)

mapper.update:
UPDATE province_num_manage
name = #{name}, value = #{value},
WHERE id = #{id};

mapper.select:
SELECT  FROM province_num_manage su where 1 = 1
  and su.id = #{id}  
  and su.name like '%${name}%'  
  and su.value = #{value}

SELECT  FROM province_num_manage su where 1 = 1
  and su.id = #{pd.id}  
  and su.name like '%${pd.name}%'  
  and su.value = #{pd.value}
