mapper.update:
UPDATE region_manage
name = #{name}, a_status = #{a_status}, a_num = #{a_num}, yc_num = #{yc_num},
WHERE id = #{id};

mapper.select:
SELECT  FROM region_manage su where 1 = 1
  and su.id = #{id}  
  and su.name like '%${name}%'  
  and su.a_status = #{a_status}  
  and su.a_num = #{a_num}  
  and su.yc_num = #{yc_num}

SELECT  FROM region_manage su where 1 = 1
  and su.id = #{pd.id}  
  and su.name like '%${pd.name}%'  
  and su.a_status = #{pd.a_status}  
  and su.a_num = #{pd.a_num}  
  and su.yc_num = #{pd.yc_num}
